  mutate(PER = (points + rebounds + assists + steals + blocks) - (field_goals_attempted - field_goals_made) - (free_throws_attempted - free_throws_made) - turnovers)

topplayers23 &lt;- player23 |&gt;
  filter(athlete_display_name == "James Harden"|athlete_display_name == "LamMelo Ball" | athlete_display_name == "Darius Garland" | athlete_display_name == "Chris Paul" | athlete_display_name == "Damien Lillard" | athlete_display_name == "Trae Young" | athlete_display_name == "Luka Doncic" | athlete_display_name == "Kyrie Irving" | athlete_display_name == "Stephen Curry" | athlete_display_name == "Ja Morant")

kyrie23 &lt;- player23 |&gt; 
  filter(athlete_display_name == "Kyrie Irving")

before &lt;- topplayers23 |&gt;
filter(game_date &lt;= as.Date("2022-11-03")) |&gt;
  group_by(athlete_display_name) |&gt;
  mutate(TS = (points/(2*(field_goals_attempted + (.44*free_throws_attempted)) )))|&gt;
  summarize(avgTS = mean(TS, na.rm = TRUE))

beforekyrie &lt;- kyrie23 |&gt;
filter(game_date &lt;= as.Date("2022-11-03")) |&gt;
  group_by(athlete_display_name) |&gt;
  mutate(TS = (points/(2*(field_goals_attempted + (.44*free_throws_attempted)) )))|&gt;
  summarize(avgTS = mean(TS, na.rm = TRUE))

after &lt;- topplayers23 |&gt;
filter(game_date &gt;= as.Date("2022-11-03")) |&gt;
  group_by(athlete_display_name) |&gt;
  mutate(TS = (points/(2*(field_goals_attempted + (.44*free_throws_attempted)) )))|&gt;
  summarize(avgTS = mean(TS, na.rm = TRUE))

afterkyrie &lt;- kyrie23 |&gt;
  filter(game_date &gt;= as.Date("2022-11-03")) |&gt;
  group_by(athlete_display_name) |&gt;
  mutate(TS = (points/(2*(field_goals_attempted + (.44*free_throws_attempted)) )))|&gt;
  summarize(avgTS = mean(TS, na.rm = TRUE))

before|&gt;
  select(athlete_display_name, avgTS) |&gt;
  top_n(10, wt=avgTS) |&gt;
  arrange(desc(avgTS))|&gt;
  gt()  |&gt;
  cols_label(
    athlete_display_name = "Player",
    avgTS = "True Shooting"
  ) |&gt;
  tab_header(
    title = "Before Kyrie's suspension",
    subtitle= "Kyrie Irving was suspended by the Brooklyn Nets on November 3rd, 2022."
  )|&gt;
  tab_source_note(
    source_note = md("Data as of Nov. 17. **Source:** HoopR | **By:** Jackson Ingvoldstad")
  ) |&gt; 
  tab_style(
    style = cell_text(color = "black", weight = "bold", align = "left"),
    locations = cells_title("title")
  ) |&gt; tab_style(
    style = cell_text(color = "black", align = "left"),
    locations = cells_title("subtitle")
  ) |&gt;
  tab_style(
     locations = cells_column_labels(columns = everything()),
     style = list(
       cell_borders(sides = "bottom", weight = px(3)),
       cell_text(weight = "bold", size=12)
     ) )|&gt;
        tab_style(
    style = list(
      cell_fill(color = "black"),
      cell_text(color = "white")
    ),
    locations = cells_body(rows = athlete_display_name == "Kyrie Irving")
  ) |&gt;
    fmt_percent(
    columns = c(avgTS),
    decimals = 1
  )








